(()=>{"use strict";class m extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"}),this.shadowRoot.innerHTML="<div></div>"}connectedCallback(){this.shadowRoot.innerHTML=this.template}}class n extends m{constructor(){super(),this.title="Exercice 4",this.className="exercise-4"}get template(){const m=this.sections.reduce(((m,n)=>m+`\n<section>\n  <h4>${n.title}</h4>\n  <div>${n.content}\n</section>\n`),"");return`\n<div class="${this.className}">\n  <h3>${this.title}</h3>    \n  \n  ${m}\n</div>\n${this.inlineStyle}\n\n`}get sections(){return[{title:"1ère fonction",content:"\n  <div>\n    <p>Étudier les variations de <math>\n      <mi>f</mi>(<mi>x</mi>) = 5<mi>x</mi> + 1\n    </math> sur <b>R</b></p>\n    <p><math>\n      <mi>f</mi><mn>'</mn>(<mi>x</mi>) = 5 > 0\n    </math> donc:</p>\n  </div>\n\n  <div>\n    \x3c!--  | x | -∞ | 3/2 |  +∞ |\n          |---|----|-----|-----|\n          | f'|       0\n          | f |       /\n    --\x3e\n    <math>\n      <mtable frame=\"solid\">\n        <mtr>\n          <mtd><mi>\n            <math><mi>x</mi></math>\n          </mi></mtd>\n          <mtd><mi>-∞</mi></mtd>\n          <mtd><mi></mi></mtd>\n          <mtd><mi>+∞</mi></mtd>\n        </mtr>\n        <mtr>\n          <mtd><mi>f</mi><mn>'</mn></mtd>\n          <mtd><mi></mi></mtd>\n          <mtd><mi>0</mi></mtd>\n          <mtd><mi></mi></mtd>\n        </mtr>\n        <mtr>\n          <mtd><mi>f</mi></mtd>\n          <mtd><mi></mi></mtd>\n          <mtd><mi>/</mi></mtd>\n          <mtd><mi></mi></mtd>\n        </mtr>\n      </mtable>\n    </math>\n  </div>\n          "},{title:"2ème fonction",content:"\n<div>\n  <p>Étudier les variations de <math>\n    <mi>f</mi>(<mi>x</mi>) = <msup><mi>x</mi><mn>2</mn></msup> - 3<mi>x</mi> +2\n  </math> sur <b>R</b>.</p>\n  <p>\n    <math><mi>f</mi><mn>'</mn>(<mi>x</mi>) = 2<mi>x</mi> - 3\n    </math> et <math>\n      2<mi>x</mi> - 3 > 0\n    </math> si <math>\n      <mi>x</mi> <mfrac><mrow>3</mrow><mrow>2</mrow></mfrac>\n    </math>  donc:</p>\n</div>\n\n<div>\n  \x3c!--  | x | -∞ | 3/2 |  +∞ |\n        |---|----|-----|-----|\n        | f'|    -  0     +  |\n        | f |    \\       /  |\n  --\x3e\n  <math>\n    <mtable frame=\"solid\">\n      <mtr>\n        <mtd><mi>\n          <math><mi>x</mi></math>\n        </mi></mtd>\n        <mtd><mi>-∞</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>\n          <math><mfrac><mrow>3</mrow><mrow>2</mrow></mfrac></math>\n        </mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+∞</mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>f</mi><mn>'</mn></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>-</mi></mtd>\n        <mtd><mi>0</mi></mtd>\n        <mtd><mi>+</mi></mtd>\n        <mtd><mi></mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>f</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>\\</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>/</mi></mtd>\n        <mtd><mi></mi></mtd>\n      </mtr>\n    </mtable>\n  </math>\n</div>  \n"},{title:"3ème fonction",content:"\n<div>\n  <p>Étudier les variations de <math>\n    <mi>f</mi>(<mi>x</mi>) = <msup><mi>x</mi><mn>3</mn></msup> - 3<msup><mi>x</mi><mn>2</mn></msup> + 2<mi>x</mi> - 3\n  </math> sur <b>R</b>.</p>\n  <p><math>\n    <mi>f</mi><mn>'</mn>(<mi>x</mi>) = 3<msup><mi>x</mi><mn>2</mn></msup> - 6<mi>x</mi> + 2\n  </math> et <math>\n    Δ = 36 - 24 = 12\n  </math></p>\n  <p>Il y a 2 racines <math>\n    <msub><mi>x</mi><mn>1</mn></msub>\n  </math> et <math>\n    <msub><mi>x</mi><mn>2</mn></msub>\n  </math>.</p>\n  <p>\n    <math>\n      <msub>\n        <mi>x</mi>\n        <mn>1</mn>\n      </msub>\n      =\n      <mfrac>\n        <mrow>(6 + <msqrt>2</msqrt>)</mrow>\n        <mrow>6</mrow>\n      </mfrac>\n      = 1.6\n    </math>\n    et\n    <math>\n      <msub>\n        <mi>x</mi>\n        <mn>2</mn>\n      </msub>\n      =\n      <mfrac>\n        <mrow>(6 - 2<msqrt>3</msqrt>)</mrow>\n        <mrow>6</mrow>\n      </mfrac>\n      = 0.4\n    </math>\n  </p>\n\n  \x3c!--  | x | -∞ | x2  | x1  |  +∞ |\n        |---|----|-----|-----|-----|\n        | f'|    +  0  -  0  +     |\n        | f |    /     \\    /     |\n  --\x3e\n  <math>\n    <mtable frame=\"solid\">\n      <mtr>\n        <mtd><mi>\n          <math><mi>x</mi></math>\n        </mi></mtd>\n        <mtd><mi>-∞</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>x2</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>x3</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+∞</mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>f</mi><mn>'</mn></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+</mi></mtd>\n        <mtd><mi>0</mi></mtd>\n        <mtd><mi>-</mi></mtd>\n        <mtd><mi>0</mi></mtd>\n        <mtd><mi>+</mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>f</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>/</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>\\</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>/</mi></mtd>\n      </mtr>\n    </mtable>\n  </math>\n\n</div>        \n"},{title:"4e fonction",content:"\n<div>\n  <p>\n    Étudier les variations de\n    <math>\n      <mi>f</mi>(<mi>x</mi>) =\n      <mfrac>\n        <mrow>3<mi>x</mi> - 1</mrow>\n        <mrow>-2<mi>x</mi> + 5</mrow>\n      </mfrac>\n    </math>\n    sur\n    <math>\n      ]-∞ ;\n      <mfrac>\n        <mrow>5</mrow>\n        <mrow>2</mrow>\n      </mfrac>\n      [ U ]\n      <mfrac>\n        <mrow>5</mrow>\n        <mrow>2</mrow>\n      </mfrac>\n      ; +∞ \n    </math>.\n  </p>\n  <p>\n    <math>\n      <mi>f</mi><mn>'</mn>(<mi>x</mi>) =\n      <mfrac>\n        <mrow>3 (-2<mi>x</mi> + 5) - (3<mi>x</mi> - 1) (-2)</mrow>\n        <mrow>\n          <msup>\n            <mi>(-2<mi>x</mi> + 5)</mi><mn>2</mn>\n          </msup>\n        </mrow>\n      </mfrac>\n      =\n      <mfrac>\n        <mrow>13</mrow>\n        <mrow>\n          <msup>\n            <mi>(-2<mi>x</mi> + 5)</mi><mn>2</mn>\n          </msup>\n        </mrow>\n      </mfrac>\n      > 0\n    </math>\n  </p>\n</div>\n\n<div>\n  \x3c!--  | x | -∞ | 5/2 |  +∞ |\n        |---|----|-----|-----|\n        | f'|  +          +  |\n        | f |  /          /  |\n  --\x3e\n  <math>\n    <mtable frame=\"solid\">\n      <mtr>\n        <mtd>\n          <mi>\n            <math><mi>x</mi></math>\n          </mi>\n        </mtd>\n        <mtd><mi>-∞</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd>\n          <mi>\n            <math>\n              <mfrac><mrow>5</mrow><mrow>2</mrow></mfrac>\n            </math>\n          </mi>\n        </mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+∞</mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>f</mi><mn>'</mn></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+</mi></mtd>\n        <mtd><mi></mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>f</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>/</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>/</mi></mtd>\n        <mtd><mi></mi></mtd>\n      </mtr>\n    </mtable>\n  </math>\n</div>\n"},{title:"5ème fonction",content:"\n<div>\n  <p>\n    Étudier les variations de\n    <math><mi>f</mi>(<mi>x</mi>) = (2<mi>x</mi> + 1)</math>,\n    <math><mi>ln</mi>(<mi>x</mi>)</math> sur <b>R*+</b>\n    .\n  </p>\n  <p>\n    <math>\n      <mi>f</mi><mn>´</mn>(<mi>x</mi>) =\n      <mfrac>\n        <mrow>2<mi>ln</mi>(<mi>x</mi>) + (2<mi>x</mi> + 1)</mrow>\n        <mrow>\n          <mi>x</mi>\n        </mrow>\n      </mfrac>\n    </math>\n  </p>\n  <p>\n    On la nomme\n    <math>\n      <mi>g</mi>(<mi>x</mi>) = 2<mi>ln</mi>(<mi>x</mi>) + 2 +\n      <mfrac>\n        <mrow>1</mrow>\n        <mrow><mi>x</mi></mrow>\n      </mfrac>\n    </math>\n    .\n  </p>\n  <p>\n    <math>\n      <mi>g</mi><mn>´</mn>(<mi>x</mi>) =\n      <mfrac>\n        <mrow>2</mrow>\n        <mrow>x</mrow>\n      </mfrac>\n      -\n      <mfrac>\n        <mrow>1</mrow>\n        <mrow>\n          <msup><mi>x</mi><mn>2</mn></msup>\n        </mrow>\n      </mfrac>\n      =\n      <mfrac>\n        <mrow>2<mi>x</mi> - 1</mrow>\n        <mrow>\n          <msup><mi>x</mi><mn>2</mn></msup>\n        </mrow>\n      </mfrac>\n      > 0\n    </math>\n    si\n    <math>\n      <mi>x</mi>\n      <mfrac>\n        <mrow>1</mrow>\n        <mrow>2</mrow>\n      </mfrac>\n    </math>\n  </p>\n  <p>\n    <math>\n      <mi>g</mi>(\n      <mfrac>\n        <mrow>1</mrow>\n        <mrow>2</mrow>\n      </mfrac>\n      ) = <mi>ln</mi>(0.5) + 4 = 3.3\n    </math>\n  </p>\n</div>\n\n<div>\n  \x3c!--  | x |  0 | 1/2 |  +∞ |\n        |---|----|-----|-----|\n        | g'|  -    0     +  |\n        | g |            /  |\n  --\x3e\n  <math>\n    <mtable frame=\"solid\">\n      <mtr>\n        <mtd>\n          <mi>\n            <math><mi>x</mi></math>\n          </mi>\n        </mtd>\n        <mtd><mi>-∞</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd>\n          <mi>\n            <math>\n              <mfrac><mrow>1</mrow><mrow>2</mrow></mfrac>\n            </math>\n          </mi>\n        </mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+∞</mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>g</mi><mn>'</mn></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>-</mi></mtd>\n        <mtd><mi>0</mi></mtd>\n        <mtd><mi>+</mi></mtd>\n        <mtd><mi></mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>g</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>/</mi></mtd>\n        <mtd><mi></mi></mtd>\n      </mtr>\n    </mtable>\n  </math>\n</div>\n<br />\n\n<div>\n  \x3c!--  | x |  0 | +∞ |\n        |---|----|----|\n        | f'|    +    |\n        | f |    /    |  \n  --\x3e\n  <math>\n    <mtable frame=\"solid\">\n      <mtr>\n        <mtd>\n          <mi>\n            <math><mi>x</mi></math>\n          </mi>\n        </mtd>\n        <mtd><mi>0</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+∞</mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>f</mi><mn>'</mn></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>+</mi></mtd>\n        <mtd><mi></mi></mtd>\n      </mtr>\n      <mtr>\n        <mtd><mi>f</mi></mtd>\n        <mtd><mi></mi></mtd>\n        <mtd><mi>/</mi></mtd>\n        <mtd><mi></mi></mtd>\n      </mtr>\n    </mtable>\n  </math>\n</div>     \n        \n"}]}get inlineStyle(){return`\n  <style>\n    .${this.className} {\n      display: flex;\n      flex-flow: row wrap;\n      align-items: flex-start;\n    }\n\n    .${this.className} h3 {\n      min-width: 100%;\n    }\n\n    section {\n      text-align: left;\n      width: 50%;\n      padding: 1rem;\n      box-sizing: border-box;\n    }\n\n    math {\n      display: inline;\n    }\n  \n    table,\n    td,\n    th {\n      border: 1px solid black;\n    }\n  \n    mtable > mtr > mtd:first-child {\n      border-right: 1px solid black;\n    }\n    mtable > mtr:first-child > mtd {\n      border-bottom: 1px solid black;\n    }\n  </style>  \n  `}}customElements.define("exercice-4",n),window.addEventListener("DOMContentLoaded",(()=>{console.log("hello maths"),document.querySelector("#app").appendChild(new n),console.log((new n).title)}))})();